mapper.select:
SELECT NO, STOCK_NAME, STOCK_QUANTITY, DIVIDEND_CYCLE, PURCHASE_PRICE, DIVIDEND_AMOUNT, CLSCD, RGSTDATE FROM STOCKPORTFOLIO where CLSCD != 'N'
and STOCK_NAME = #{stockName}
and DIVIDEND_CYCLE = #{dividendCycle}
order by NO DESC, RGSTDATE desc

mapper.insert:
INSERT 	INTO	STOCKPORTFOLIO
        (       NO,
                STOCK_NAME,
                STOCK_QUANTITY,
                DIVIDEND_CYCLE,
                PURCHASE_PRICE,
                DIVIDEND_AMOUNT,
                RGSTDATE)
        VALUES(
                STOCKPORTFOLIO_SEQ.NEXTVAL
                ,#{stockName}
                ,#{stockQuantity}
                ,#{dividendCycle}
                ,#{purchasePrice}
                ,#{dividendAmount}
                ,sysdate
        )
